target.groovy:
import groovy.text.SimpleTemplateEngine
import gherkin.I18n

def engine = new SimpleTemplateEngine()
def templateSource = new File('src/main/code_generator/I18n.java.txt').getText()

I18n.all.each { i18n ->
    i18n.codeKeywords.each { kw ->
        def binding = ["i18n":i18n, "kw":kw]
        template = engine.createTemplate(templateSource).make(binding)
        def file = new File("src/main/java/cucumber/annotation/${i18n.underscoredIsoCode}/${kw}.java")
        file.parentFile.mkdirs()
        file.write(template.toString(), "UTF-8")
    }
}
